mapper.delete:

delete from ec_assets_per
where cust_no = #{custNo,jdbcType=VARCHAR}
  and seq_no = #{seqNo,jdbcType=INTEGER}

mapper.select:

select cust_no, seq_no, assets_nam, assets_typ, stored_amt, obtain_time, expire_time, 
establish_time, principal, create_date, create_time, create_user, update_date, update_time, 
update_user, time_stamp, rec_stat
from ec_assets_per
where cust_no = #{custNo,jdbcType=VARCHAR}
  and seq_no = #{seqNo,jdbcType=INTEGER}
