mapper.delete:

delete from ec_cust_agent
where cust_no = #{custNo,jdbcType=VARCHAR}
  and orig_sys_agent_no = #{origSysAgentNo,jdbcType=VARCHAR}

mapper.select:

select cust_no, orig_sys_agent_no, agent_devcom_cd, agent_kind, bln_chnl, perf_grade, 
credit_grade, inwork_date, agent_sts, outwork_date, posit_grade, create_date, create_time, 
create_user, update_date, update_time, update_user, time_stamp, rec_stat
from ec_cust_agent
where cust_no = #{custNo,jdbcType=VARCHAR}
  and orig_sys_agent_no = #{origSysAgentNo,jdbcType=VARCHAR}
