mapper.select:
select cust_no, trade, unit_nature, unit_nam, department, headship, position, post_grade, work_desc, entry_date, leave_date, work_nature, create_date, create_time, create_user, update_date, update_time, update_user, time_stamp, rec_stat from ec_cust_career where cust_no = #{custNo,jdbcType=VARCHAR}
and unit_nam like concat('%', #{unitNam,jdbcType=VARCHAR}, '%')
and position like concat('%', #{position,jdbcType=VARCHAR}, '%')


select cust_no
from ec_cust_career
where cust_no = #{custNo,jdbcType=VARCHAR}
