mapper.delete:

delete from ec_cust_cert
where cust_no = #{custNo,jdbcType=VARCHAR}
  and cert_sqn = #{certSqn,jdbcType=INTEGER}

mapper.select:

select cust_no, cert_sqn, cert_typ, cert_no, acti_date, stop_use_date, cert_eff_date, 
desc_content, cert_issued_plece, orig_sys_flg, cert_sts, create_date, create_time, 
create_user, update_date, update_time, update_user, time_stamp, rec_stat
from ec_cust_cert
where cust_no = #{custNo,jdbcType=VARCHAR}
  and cert_sqn = #{certSqn,jdbcType=INTEGER}

select a.cust_no, a.cert_sqn, b.cust_nam,a.cert_typ, a.cert_no, a.acti_date, a.stop_use_date, a.cert_eff_date, a.desc_content, a.cert_issued_plece, a.orig_sys_flg, a.cert_sts, a.create_date, a.create_time, a.create_user, a.update_date, a.update_time, a.update_user, a.time_stamp, a.rec_stat from ec_cust_cert a, ec_cust_name b where a.cust_no = b.cust_no and a.cust_no = #{custNo,jdbcType=VARCHAR}
and cert_typ = #{certTyp,jdbcType=VARCHAR}
and cert_no like concat('%', #{certNo,jdbcType=VARCHAR}, '%')
ORDER BY a.cert_sqn

    select a.cust_no, a.cert_sqn, b.cust_nam,a.cert_typ, a.cert_no, a.acti_date, a.stop_use_date, a.cert_eff_date, a.desc_content, 
	    a.cert_issued_plece, a.orig_sys_flg, a.cert_sts, a.create_date, a.create_time, a.create_user, 
	    a.update_date, a.update_time, a.update_user, a.time_stamp, a.rec_stat
    from ec_cust_cert a, ec_cust_name b
		where a.cust_no = b.cust_no
			and a.cust_no = #{arg0,jdbcType=VARCHAR}
			and a.cert_typ = #{arg1,jdbcType=VARCHAR}
      		and a.cert_no = #{arg2,jdbcType=VARCHAR}
			and a.rec_stat='0' and b.rec_stat='0'
		ORDER BY a.cert_sqn


select cust_no
from ec_cust_cert
where cust_no = #{custNo,jdbcType=VARCHAR}
