mapper.delete:

delete from ec_cust_collaborator
where cust_no = #{custNo,jdbcType=VARCHAR}
  and collaborator = #{collaborator,jdbcType=VARCHAR}

mapper.select:

select cust_no, collaborator, collaborate_date, create_user, create_date, create_time, 
update_user, update_date, update_time, time_stamp, rec_stat
from ec_cust_collaborator
where cust_no = #{custNo,jdbcType=VARCHAR}
  and collaborator = #{collaborator,jdbcType=VARCHAR}
