mapper.delete:

delete from ec_cust_edu
where cust_no = #{custNo,jdbcType=VARCHAR}
  and edu_typ = #{eduTyp,jdbcType=VARCHAR}

mapper.select:

select cust_no, edu_typ, school_nam, edu_cd, degree_cd, speciality1, speciality2, 
speciality3, entrance_date, graduate_date, create_date, create_time, create_user, 
update_date, update_time, update_user, time_stamp, rec_stat
from ec_cust_edu
where cust_no = #{custNo,jdbcType=VARCHAR}
  and edu_typ = #{eduTyp,jdbcType=VARCHAR}

select cust_no, edu_typ, school_nam, edu_cd, degree_cd, speciality1, speciality2, speciality3, entrance_date, graduate_date, create_date, create_time, create_user, update_date, update_time, update_user, time_stamp, rec_stat from ec_cust_edu where cust_no = #{custNo,jdbcType=VARCHAR}
and edu_typ = #{eduTyp,jdbcType=VARCHAR}
and school_nam like concat('%', #{schoolNam,jdbcType=VARCHAR}, '%')
order by edu_typ
