mapper.delete:

delete from ec_cust_event
where cust_no = #{custNo,jdbcType=VARCHAR}
  and event_type = #{eventType,jdbcType=VARCHAR}
  and event_date = #{eventDate,jdbcType=VARCHAR}

mapper.insert:

insert into ec_cust_event (cust_no, event_type, event_date, 
  event_desc, create_date, create_time, 
  create_user, update_date, update_time, 
  update_user, time_stamp, rec_stat
  )
values (#{custNo,jdbcType=VARCHAR}, #{eventType,jdbcType=VARCHAR}, #{eventDate,jdbcType=VARCHAR}, 
  #{eventDesc,jdbcType=VARCHAR}, #{createDate,jdbcType=VARCHAR}, #{createTime,jdbcType=VARCHAR}, 
  #{createUser,jdbcType=VARCHAR}, #{updateDate,jdbcType=VARCHAR}, #{updateTime,jdbcType=VARCHAR}, 
  #{updateUser,jdbcType=VARCHAR}, #{timeStamp,jdbcType=TIMESTAMP}, #{recStat,jdbcType=VARCHAR}
  )

mapper.update:

update ec_cust_event
set event_desc = #{eventDesc,jdbcType=VARCHAR},
  create_date = #{createDate,jdbcType=VARCHAR},
  create_time = #{createTime,jdbcType=VARCHAR},
  create_user = #{createUser,jdbcType=VARCHAR},
  update_date = #{updateDate,jdbcType=VARCHAR},
  update_time = #{updateTime,jdbcType=VARCHAR},
  update_user = #{updateUser,jdbcType=VARCHAR},
  time_stamp = #{timeStamp,jdbcType=TIMESTAMP},
  rec_stat = #{recStat,jdbcType=VARCHAR}
where cust_no = #{custNo,jdbcType=VARCHAR}
  and event_type = #{eventType,jdbcType=VARCHAR}
  and event_date = #{eventDate,jdbcType=VARCHAR}

mapper.select:

select cust_no, event_type, event_date, event_desc, create_date, create_time, create_user, 
update_date, update_time, update_user, time_stamp, rec_stat
from ec_cust_event
where cust_no = #{custNo,jdbcType=VARCHAR}
  and event_type = #{eventType,jdbcType=VARCHAR}
  and event_date = #{eventDate,jdbcType=VARCHAR}

select cust_no, event_type, event_date, event_desc, create_date, create_time, create_user, update_date, update_time, update_user, time_stamp, rec_stat from ec_cust_event where cust_no = #{custNo,jdbcType=VARCHAR} and rec_stat = '0'
and event_type = #{eventType,jdbcType=VARCHAR}
and event_date = #{eventDate,jdbcType=VARCHAR}
and event_desc like concat('%', #{eventDesc,jdbcType=VARCHAR}, '%')
