mapper.delete:

delete from ec_cust_habitavo
where cust_no = #{custNo,jdbcType=VARCHAR}
  and habit_avoc_cd = #{habitAvocCd,jdbcType=VARCHAR}

mapper.select:

select cust_no, habit_avoc_cd, create_date, create_time, create_user, update_date, 
update_time, update_user, time_stamp, rec_stat
from ec_cust_habitavo
where cust_no = #{custNo,jdbcType=VARCHAR}
  and habit_avoc_cd = #{habitAvocCd,jdbcType=VARCHAR}
