mapper.select:
select contact_no, cust_no, deal_person, interac_mode, rec_date, interac_theme_desc, addi_desc, interac_date, invalid_flg, interac_sts_cd, feedback_typ, cust_interac_flg, cust_interac_type, data_source, create_date, create_time, create_user, update_date, update_time, update_user, time_stamp, rec_stat from ec_cust_touch where cust_no = #{custNo,jdbcType=VARCHAR} and rec_stat = '0'
and contact_no = #{contactNo,jdbcType=VARCHAR}
and deal_person like concat('%', #{dealPerson,jdbcType=VARCHAR}, '%')
