mapper.delete:

delete from ec_diff_cust
where task_id = #{taskId,jdbcType=VARCHAR}
  and source_cust_no = #{sourceCustNo,jdbcType=VARCHAR}
  and target_cust_no = #{targetCustNo,jdbcType=VARCHAR}

mapper.select:

select task_id, source_cust_no, target_cust_no, mark_user, mark_reason, mark_time, 
mark_conclusion, create_date, create_time, create_user, update_date, update_time, 
update_user, time_stamp, rec_stat
from ec_diff_cust
where task_id = #{taskId,jdbcType=VARCHAR}
  and source_cust_no = #{sourceCustNo,jdbcType=VARCHAR}
  and target_cust_no = #{targetCustNo,jdbcType=VARCHAR}

    select task_id, source_cust_no, target_cust_no, mark_user, mark_reason, mark_time,
    mark_conclusion, create_date, create_time, create_user, update_date, update_time,
    update_user, time_stamp, rec_stat
    from ec_diff_cust
    where 1=1 

		and task_id = #{taskId}


		and source_cust_no = #{sourceCustNo}


		and target_cust_no = #{targetCustNo}
