mapper.delete:

delete from ec_finace_claim
where cust_no = #{custNo,jdbcType=VARCHAR}
  and claim_no = #{claimNo,jdbcType=VARCHAR}
  and policy_no = #{policyNo,jdbcType=VARCHAR}
  and closed_date = #{closedDate,jdbcType=VARCHAR}

mapper.select:

select cust_no, claim_no, policy_no, closed_date, accept_date, claim_type, kind_name, 
claim_sts, status_upt_tm, beneficiary, indemnity_amt, profit_amt, profit_pro, pay_bank, 
pay_ac, pay_ac_last_four, arrival_date, claim_desc, create_date, create_time, create_user, 
update_date, update_time, update_user, time_stamp, rec_stat
from ec_finace_claim
where cust_no = #{custNo,jdbcType=VARCHAR}
  and claim_no = #{claimNo,jdbcType=VARCHAR}
  and policy_no = #{policyNo,jdbcType=VARCHAR}
  and closed_date = #{closedDate,jdbcType=VARCHAR}

select cust_no, claim_no, policy_no, closed_date, accept_date, claim_type, kind_name, claim_sts, status_upt_tm, beneficiary, indemnity_amt, profit_amt, profit_pro, pay_bank, pay_ac, pay_ac_last_four, arrival_date, claim_desc, create_date, create_time, create_user, update_date, update_time, update_user, time_stamp, rec_stat from ec_finace_claim where cust_no = #{custNo,jdbcType=VARCHAR} and rec_stat = '0'
and policy_no = #{policyNo,jdbcType=VARCHAR}
and beneficiary like concat('%', #{beneficiary,jdbcType=VARCHAR}, '%')
