mapper.delete:

delete from ec_finace_pay
where cust_no = #{custNo,jdbcType=VARCHAR}
  and policy_no = #{policyNo,jdbcType=VARCHAR}
  and pay_date = #{payDate,jdbcType=VARCHAR}
  and pay_chnl = #{payChnl,jdbcType=VARCHAR}

mapper.select:

select cust_no, policy_no, pay_date, pay_chnl, pay_bank, pay_account, pay_ac_last_four, 
pay_years, pay_tm, pay_trequency, pay_amt, payable_date, pay_mode, payer, applicant, 
create_date, create_time, create_user, update_date, update_time, update_user, time_stamp, 
rec_stat
from ec_finace_pay
where cust_no = #{custNo,jdbcType=VARCHAR}
  and policy_no = #{policyNo,jdbcType=VARCHAR}
  and pay_date = #{payDate,jdbcType=VARCHAR}
  and pay_chnl = #{payChnl,jdbcType=VARCHAR}

select cust_no, policy_no, pay_date, pay_chnl, pay_bank, pay_account, pay_ac_last_four, pay_years, pay_tm, pay_trequency, pay_amt, payable_date, pay_mode, payer, applicant, create_date, create_time, create_user, update_date, update_time, update_user, time_stamp, rec_stat from ec_finace_pay where cust_no = #{custNo,jdbcType=VARCHAR} and rec_stat = '0'
and policy_no = #{policyNo,jdbcType=VARCHAR}
and payer like concat('%', #{payer,jdbcType=VARCHAR}, '%')
