mapper.delete:

delete from ec_first_act
where cust_no = #{custNo,jdbcType=VARCHAR}
  and bank_cd = #{bankCd,jdbcType=VARCHAR}
  and bank_ac = #{bankAc,jdbcType=VARCHAR}

mapper.select:

select cust_no, bank_cd, bank_ac, wht_def_ac, bank_ac_nam, prov_cd, city_cd, create_date, 
create_time, create_user, update_date, update_time, update_user, time_stamp, rec_stat
from ec_first_act
where cust_no = #{custNo,jdbcType=VARCHAR}
  and bank_cd = #{bankCd,jdbcType=VARCHAR}
  and bank_ac = #{bankAc,jdbcType=VARCHAR}
