mapper.delete:

delete from ec_health_file
where chk_date = #{chkDate,jdbcType=VARCHAR}
  and chk_no = #{chkNo,jdbcType=VARCHAR}
  and cust_no = #{custNo,jdbcType=VARCHAR}

mapper.select:

select chk_date, chk_no, cust_no, med_org_nam, consul_room, principal_doctor, file_typ, 
chek_content, file_url, file_blob, create_date, create_time, create_user, update_date, update_time, 
update_user, time_stamp, rec_stat
from ec_health_file
where chk_date = #{chkDate,jdbcType=VARCHAR}
  and chk_no = #{chkNo,jdbcType=VARCHAR}
  and cust_no = #{custNo,jdbcType=VARCHAR}

select chk_date, chk_no, cust_no, med_org_nam, consul_room, principal_doctor, file_typ, chek_content, file_url, file_blob, create_date, create_time, create_user, update_date, update_time, update_user, time_stamp, rec_stat from ec_health_file where cust_no = #{custNo,jdbcType=VARCHAR} and rec_stat = '0'
and chk_no = #{chkNo,jdbcType=VARCHAR}
and chek_content like concat('%', #{chekContent,jdbcType=VARCHAR}, '%')
