mapper.select:

select import_det_cd, import_cd, import_content, import_sts, import_reason, create_date, 
create_time, create_user, update_date, update_time, update_user, time_stamp, rec_stat
from ec_import_dets
where import_cd = #{importCd,jdbcType=VARCHAR}

	and import_sts = #{importSts,jdbcType=VARCHAR}

order by time_stamp desc
