mapper.delete:

delete from ec_integral
where cust_no = #{custNo,jdbcType=VARCHAR}
  and policy_no = #{policyNo,jdbcType=VARCHAR}
  and integ_chg_typ = #{integChgTyp,jdbcType=VARCHAR}

mapper.select:

select cust_no, policy_no, integ_chg_typ, integ_chg_quota, integ_nam, integ_chg_time, 
product_nam, exp_time, remark, create_date, create_time, create_user, update_date, update_time, 
update_user, time_stamp, rec_stat
from ec_integral
where cust_no = #{custNo,jdbcType=VARCHAR}
  and policy_no = #{policyNo,jdbcType=VARCHAR}
  and integ_chg_typ = #{integChgTyp,jdbcType=VARCHAR}

select cust_no, policy_no, integ_chg_typ, integ_chg_quota, integ_nam, integ_chg_time, product_nam, exp_time, remark, create_date, create_time, create_user, update_date, update_time, update_user, time_stamp, rec_stat from ec_integral where cust_no = #{custNo,jdbcType=VARCHAR} and rec_stat = '0'
and policy_no = #{policyNo,jdbcType=VARCHAR}
and integ_chg_typ = #{integChgTyp,jdbcType=VARCHAR}
and integ_nam like concat('%', #{integNam,jdbcType=VARCHAR}, '%')
and product_nam like concat('%', #{productNam,jdbcType=VARCHAR}, '%')
