mapper.delete:

delete from ec_merge_split_trace
where trace_id = #{traceId,jdbcType=INTEGER}
  and task_id = #{taskId,jdbcType=VARCHAR}

mapper.select:

select trace_id, task_id, merge_split_action, deal_user, deal_time, deal_conclusion, 
deal_opinion, create_date, create_time, create_user, update_date, update_time, update_user, 
time_stamp, rec_stat
from ec_merge_split_trace
where trace_id = #{traceId,jdbcType=INTEGER}
  and task_id = #{taskId,jdbcType=VARCHAR}

select trace_id, task_id, merge_split_action, deal_user, deal_time, deal_conclusion, deal_opinion, create_date, create_time, create_user, update_date, update_time, update_user, time_stamp, rec_stat from ec_merge_split_trace where 1=1
and trace_id = #{traceId}
and task_id = #{taskId}
order by time_stamp desc
