mapper.select:
select product_cd, product_nam, product_typ, product_ver, duty, premium, amount, sale_chnl, eff_date, exp_date, create_date, create_time, create_user, update_date, update_time, update_user, time_stamp, rec_stat from ec_product where 1=1
and product_cd = #{productCd,jdbcType=VARCHAR}
and product_nam = #{productNam,jdbcType=VARCHAR}
