mapper.delete:

delete from ec_relation_data
where forward_relation = #{forwardRelation,jdbcType=VARCHAR}
  and sex = #{sex,jdbcType=VARCHAR}

mapper.insert:

insert into ec_relation_data (forward_relation, sex, reverse_relation
  )
values (#{forwardRelation,jdbcType=VARCHAR}, #{sex,jdbcType=VARCHAR}, #{reverseRelation,jdbcType=VARCHAR}
  )

mapper.update:

update ec_relation_data
set reverse_relation = #{reverseRelation,jdbcType=VARCHAR}
where forward_relation = #{forwardRelation,jdbcType=VARCHAR}
  and sex = #{sex,jdbcType=VARCHAR}

mapper.select:

select forward_relation, sex, reverse_relation
from ec_relation_data
where forward_relation = #{forwardRelation,jdbcType=VARCHAR}
  and sex = #{sex,jdbcType=VARCHAR}


select forward_relation, sex, reverse_relation
from ec_relation_data
where reverse_relation = #{reverseRelation,jdbcType=VARCHAR}
and sex = #{sex,jdbcType=VARCHAR}
