    approve_user,t1.split_deal_user, t1.split_approve_user,t1.rec_stat,t2.judge_condition
    from ec_sim_task t1,ec_sim_cust_rule t2
    where 
    t1.rule_id=t2.rule_id

  		and t1.deal_user = #{dealUser}


		and t1.task_id = #{taskId}


		and t1.task_state in

			#{item}



		and t1.approve_user = #{approveUser}


  		and t1.split_deal_user = #{splitDealUser}


  		and t1.split_approve_user = #{splitApproveUser}

      ORDER BY t1.task_create_time DESC

select t1.task_id, t1.rule_id, t1.similar_percent, t1.task_create_time, t1.task_state, t1.deal_user, t1.approve_user,t1.split_deal_user, t1.split_approve_user,t1.rec_stat,t2.judge_condition from ec_sim_task t1,ec_sim_cust_rule t2 where t1.rule_id=t2.rule_id and( t1.approve_user = '' or t1.approve_user is null )
and t1.deal_user = #{dealUser}
and t1.task_id = #{taskId}
and t1.task_state in
#{item}
ORDER BY t1.task_create_time DESC

select t1.task_id, t1.rule_id, t1.similar_percent, t1.task_create_time, t1.task_state, t1.deal_user, t1.approve_user,t1.split_deal_user, t1.split_approve_user,t1.rec_stat,t2.judge_condition from ec_sim_task t1,ec_sim_cust_rule t2 where t1.rule_id=t2.rule_id and( t1.split_approve_user = '' or t1.split_approve_user is null )
and t1.task_id = #{taskId}
and t1.task_state = #{taskState}
ORDER BY t1.task_create_time DESC

select t1.task_id, t1.rule_id, t1.similar_percent, t1.task_create_time, t1.task_state, t1.deal_user, t1.approve_user,t1.split_deal_user, t1.split_approve_user,t1.rec_stat,t2.judge_condition from ec_sim_task t1,ec_sim_cust_rule t2 where t1.rule_id=t2.rule_id and t1.split_approve_user = #{employeeId}
and t1.task_state = #{taskState}
ORDER BY t1.task_create_time DESC
