mapper.delete:

delete from ec_tag_includ
where tag_id = #{tagId,jdbcType=VARCHAR}
  and includ_tag_id = #{includTagId,jdbcType=VARCHAR}

mapper.select:

select tag_id, includ_tag_id, tag_type_id, tag_name, includ_tag_name, remark, create_date, 
create_time, create_user, update_date, update_time, update_user, time_stamp, rec_stat
from ec_tag_includ
where tag_id = #{tagId,jdbcType=VARCHAR}
  and includ_tag_id = #{includTagId,jdbcType=VARCHAR}

select tag_id, includ_tag_id, tag_type_id, tag_name, includ_tag_name, remark, create_date, 
create_time, create_user, update_date, update_time, update_user, time_stamp, rec_stat
from ec_tag_includ
where 1=1

 	and tag_id = #{tagId,jdbcType=VARCHAR} 

 	and includ_tag_id = #{includTagId,jdbcType=VARCHAR} 

 	and tag_name = #{tagName,jdbcType=VARCHAR} 

 	and includ_tag_name = #{includTagName,jdbcType=VARCHAR}
