mapper.select:
select track_cd, track_time, cust_no, track_typ, track_sub_typ, track_content, record_user, wht_through, contacts, phone_no, next_follow_up_tm, location, comt_flg, create_date, create_time, create_user, update_date, update_time, update_user, time_stamp, rec_stat from ec_track_info where cust_no = #{custNo,jdbcType=VARCHAR}
and track_typ = #{trackTyp,jdbcType=VARCHAR}
ORDER BY track_time DESC
