mapper.delete:

delete from sys_code_mapper
where code_type = #{codeType,jdbcType=VARCHAR}
  and code_val = #{codeVal,jdbcType=VARCHAR}
  and ext_sys_flg = #{extSysFlg,jdbcType=VARCHAR}
  and ext_code_type = #{extCodeType,jdbcType=VARCHAR}

mapper.select:

select code_type, code_val, ext_sys_flg, ext_code_type, type_name, code_name, ext_code_val, 
remark, create_date, create_time, create_user, update_date, update_time, update_user, 
time_stamp, rec_stat
from sys_code_mapper
where code_type = #{codeType,jdbcType=VARCHAR}
  and code_val = #{codeVal,jdbcType=VARCHAR}
  and ext_sys_flg = #{extSysFlg,jdbcType=VARCHAR}
  and ext_code_type = #{extCodeType,jdbcType=VARCHAR}


    select code_type, code_val, ext_sys_flg, ext_code_type, type_name, code_name, ext_code_val, 
    remark, create_date, create_time, create_user, update_date, update_time, update_user, 
    time_stamp, rec_stat
    from sys_code_mapper
    where 1=1

		and code_type = #{codeType,jdbcType=VARCHAR}


		and code_val = #{codeVal,jdbcType=VARCHAR}


		and ext_sys_flg = #{extSysFlg,jdbcType=VARCHAR}


		and ext_code_type = #{extCodeType,jdbcType=VARCHAR}


		and code_name like concat('%', #{codeName,jdbcType=VARCHAR}, '%')


		and type_name like concat('%', #{typeName,jdbcType=VARCHAR}, '%')
