mapper.select:
select county_code, county_name, province_code, city_code, full_name, county_order, valid_flag
from sys_county
where province_code = #{arg0,jdbcType=VARCHAR}
and city_code = #{arg1,jdbcType=VARCHAR}
ORDER BY county_order + 0

select county_code
  from sys_county
  where county_name = #{countyName,jdbcType=VARCHAR} and CITY_CODE=#{cityCode,jdbcType=VARCHAR}
