mapper.delete:

delete from sys_dept_posi
where dept_code = #{deptCode,jdbcType=VARCHAR}
  and posi_code = #{posiCode,jdbcType=VARCHAR}

mapper.select:

select dept_code, posi_code, posi_name,dept_posi, create_date, create_time, create_user, update_date, 
update_time, update_user, time_stamp, rec_stat
from sys_dept_posi
where dept_code = #{deptCode,jdbcType=VARCHAR}
  and posi_code = #{posiCode,jdbcType=VARCHAR}

select dept_code, posi_code, posi_name,dept_posi
from sys_dept_posi
where dept_code = #{deptCode,jdbcType=VARCHAR}

select dept_code, posi_code, posi_name,dept_posi
from sys_dept_posi
where dept_code = #{deptCode,jdbcType=VARCHAR}
and posi_code = #{posiCode,jdbcType=VARCHAR}
