mapper.delete:

delete from sys_job_msg_rela
where sched_name = #{schedName,jdbcType=VARCHAR}
  and job_name = #{jobName,jdbcType=VARCHAR}
  and job_group = #{jobGroup,jdbcType=VARCHAR}
  and msg_code = #{msgCode,jdbcType=VARCHAR}

mapper.update:

update sys_job_msg_rela
set remind_code = #{remindCode,jdbcType=VARCHAR},
  create_date = #{createDate,jdbcType=VARCHAR},
  create_time = #{createTime,jdbcType=VARCHAR},
  create_user = #{createUser,jdbcType=VARCHAR},
  update_date = #{updateDate,jdbcType=VARCHAR},
  update_time = #{updateTime,jdbcType=VARCHAR},
  update_user = #{updateUser,jdbcType=VARCHAR},
  time_stamp = #{timeStamp,jdbcType=TIMESTAMP},
  rec_stat = #{recStat,jdbcType=VARCHAR}
where sched_name = #{schedName,jdbcType=VARCHAR}
  and job_name = #{jobName,jdbcType=VARCHAR}
  and job_group = #{jobGroup,jdbcType=VARCHAR}
  and msg_code = #{msgCode,jdbcType=VARCHAR}

mapper.select:

select sched_name, job_name, job_group, msg_code, remind_code, create_date, create_time, 
create_user, update_date, update_time, update_user, time_stamp, rec_stat
from sys_job_msg_rela
where sched_name = #{schedName,jdbcType=VARCHAR}
  and job_name = #{jobName,jdbcType=VARCHAR}
  and job_group = #{jobGroup,jdbcType=VARCHAR}
  and msg_code = #{msgCode,jdbcType=VARCHAR}


select sched_name, job_name, job_group, msg_code,remind_code, create_date, create_time, create_user, 
update_date, update_time, update_user, time_stamp, rec_stat
from sys_job_msg_rela
where 1=1

  	and sched_name = #{schedName,jdbcType=VARCHAR}


  	and job_name = #{jobName,jdbcType=VARCHAR}


  	and job_group = #{jobGroup,jdbcType=VARCHAR}


  	and msg_code = #{msgCode,jdbcType=VARCHAR}


  	and remind_code = #{remindCode,jdbcType=VARCHAR}
