  and param_code = #{paramCode,jdbcType=VARCHAR}


select param_type, param_code, param_value, param_desc, create_date, create_time, 
create_user, update_date, update_time, update_user, time_stamp, rec_stat
from sys_param where rec_stat='0'

	and param_type = #{paramType,jdbcType=VARCHAR}


  and param_code = #{paramCode,jdbcType=VARCHAR}


