mapper.select:
select rule_id, advance_days, is_same_day, overdue_days, remind_type, send_obj, remind_topic, remind_tpl, remind_level, create_date, create_time, create_user, update_date, update_time, update_user, time_stamp from sys_remind_rule where 1=1
and remind_type = #{remindType}
and remind_topic like '%${remindTopic}%'
and remind_level = #{remindLevel}
order by time_stamp desc
