mapper.delete:

delete from sys_role_enter
where role_code = #{roleCode,jdbcType=VARCHAR}
  and enter_code = #{enterCode,jdbcType=VARCHAR}

mapper.select:

select role_code, enter_code, create_date, create_time, create_user, update_date, 
update_time, update_user, time_stamp, rec_stat
from sys_role_enter
where role_code = #{roleCode,jdbcType=VARCHAR}
  and enter_code = #{enterCode,jdbcType=VARCHAR}
