mapper.select:

select role_code, menu_code, role_name, menu_name, view_or_not, create_date, create_time, 
create_user, update_date, update_time, update_user, time_stamp, rec_stat
from sys_role_menu
where role_code = #{roleCode,jdbcType=VARCHAR}
  and menu_code = #{menuCode,jdbcType=VARCHAR}

select role_code, role_name, menu_code,menu_name,view_or_not, create_date, create_time, create_user, update_date, update_time, update_user, time_stamp, rec_stat from sys_role_menu where 1=1
and role_code = #{roleCode}
and role_name = #{roleName}
and menu_code like '%${menuCode}%'
and menu_name like '%${menuName}%'
