mapper.delete:

delete from sys_rule_factor_param
where param_name = #{paramName,jdbcType=VARCHAR}
  and item_code = #{itemCode,jdbcType=CHAR}

mapper.select:

select param_name, item_code, item_name, status, remark, save_object, save_field, 
update_status, create_date, create_time, create_user, update_date, update_time, update_user, 
time_stamp, rec_stat
from sys_rule_factor_param
where param_name = #{paramName,jdbcType=VARCHAR}
  and item_code = #{itemCode,jdbcType=CHAR}

select param_name, item_code, item_name, status, remark, save_object, save_field, 
 update_status, create_date, create_time, create_user, update_date, update_time, update_user, 
 time_stamp, rec_stat
 from sys_rule_factor_param where 1=1

     and param_name = #{paramName}


     and item_code = #{itemCode}


     and item_name = #{itemName}


     and status = #{status}


     and remark = #{remark}


     and save_object = #{saveObject}


     and save_field = #{saveField}


     and rec_stat = #{recStat}
