mapper.delete:

delete from sys_rule_strategy_hit
where strategy_id = #{strategyId,jdbcType=VARCHAR}
  and req_id = #{reqId,jdbcType=VARCHAR}

mapper.select:

select strategy_id, req_id, seq_no, create_date, create_time, create_user, update_date, 
update_time, update_user, time_stamp, rec_stat
from sys_rule_strategy_hit
where strategy_id = #{strategyId,jdbcType=VARCHAR}
  and req_id = #{reqId,jdbcType=VARCHAR}
