mapper.select:

select *
from sys_tag
where  tag_name = #{tagName,jdbcType=VARCHAR}

  and tag_type_id = #{tagTypeId,jdbcType=VARCHAR}

  and rec_stat = '0'

select t1.* ,t2.user_name
from sys_tag t1 left join sys_user_info t2 on t1.create_user=t2.user_id
where 1=1
and t1.rec_stat='0'

 	and tag_type_id = #{tagTypeId,jdbcType=VARCHAR} 

 	and tag_id = #{tagId,jdbcType=VARCHAR} 

 	and tag_name like concat(CONCAT('%',#{tagName,jdbcType=VARCHAR}),'%') 

 	and parent_tag_id = #{parentTagId,jdbcType=VARCHAR}

select tag_id from sys_tag where 1=1 and rec_stat='0'


		and strategy_id = #{strategyId}


		and 1=2
