mapper.select:

 select tag_type_id, tag_type_name, super_tag_type_id, tag_type_level, remark, create_date, 
create_time, create_user, update_date, update_time, update_user, time_stamp, rec_stat
from sys_tag_type
where 1=1

 	and super_tag_type_id = #{superTagTypeId,jdbcType=VARCHAR} 

 	and tag_type_level = #{tagTypeLevel,jdbcType=VARCHAR} 

 	and tag_type_name = #{tagTypeName,jdbcType=VARCHAR}
