mapper.select:

select user_id, user_name, sex, password,employee_id, user_stat,
enter_code, enter_name, dept_code,
dept_name, posi_code, posi_name,dept_posi, role_code, role_name, telphone,
email, create_date,
create_time, create_user, update_date, update_time, update_user, time_stamp,
rec_stat
from sys_user_info where rec_stat !=1 and user_stat !=1 

	and user_id = #{userId}


	and user_name like  '%${userName}%'


	and enter_code = #{enterCode}


	and dept_code = #{deptCode}


	and posi_code = #{posiCode}


	and user_stat = #{userStat}


	and employee_id like '%${employeeId}%'

select * from sys_user_info
where 1=1

	and user_id = #{user}



	and posi_code = #{userPosi}



	and dept_code = #{userDept}



	and dept_posi in

		#{item}



	and dept_code in

		#{item}



	and enter_code in

		#{item}
