mapper.delete:

delete from oc_busi_opp_cntr
where busi_opp_no = #{busiOppNo,jdbcType=VARCHAR}
  and contract_cust_no = #{contractCustNo,jdbcType=VARCHAR}

mapper.insert:

insert into oc_busi_opp_cntr (busi_opp_no, contract_cust_no, cust_no, 
  rela_date, create_user, create_date, 
  create_time, update_user, update_date, 
  update_time, time_stamp, rec_stat
  )
values (#{busiOppNo,jdbcType=VARCHAR}, #{contractCustNo,jdbcType=VARCHAR}, #{custNo,jdbcType=VARCHAR}, 
  #{relaDate,jdbcType=DATE}, #{createUser,jdbcType=VARCHAR}, #{createDate,jdbcType=VARCHAR}, 
  #{createTime,jdbcType=VARCHAR}, #{updateUser,jdbcType=VARCHAR}, #{updateDate,jdbcType=VARCHAR}, 
  #{updateTime,jdbcType=VARCHAR}, #{timeStamp,jdbcType=TIMESTAMP}, #{recStat,jdbcType=VARCHAR}
  )

mapper.select:

select busi_opp_no, contract_cust_no, cust_no, rela_date, create_user, create_date, 
create_time, update_user, update_date, update_time, time_stamp, rec_stat
from oc_busi_opp_cntr
where busi_opp_no = #{busiOppNo,jdbcType=VARCHAR}
  and contract_cust_no = #{contractCustNo,jdbcType=VARCHAR}

    select a.busi_opp_no, a.contract_cust_no, b.contract_name as contract_cust_nam, a.cust_no, a.rela_date, b.mobile, b.cust_relation
    from oc_busi_opp_cntr a, oc_cust_contract b
		where a.contract_cust_no = b.contract_no

				and a.busi_opp_no = #{busiOppNo,jdbcType=VARCHAR}


				and a.cust_no = #{custNo,jdbcType=VARCHAR}


				and a.contract_cust_no = #{contractCustNo,jdbcType=VARCHAR}


				and b.contract_name like CONCAT('%', #{contractCustNam,jdbcType=VARCHAR},'%')
