mapper.delete:

delete from oc_busi_opp_collaborator
where busi_opp_no = #{busiOppNo,jdbcType=VARCHAR}
  and collaborator = #{collaborator,jdbcType=DATE}

mapper.insert:

insert into oc_busi_opp_collaborator (busi_opp_no, collaborator, cust_no, 
  collaborate_date, create_user, create_date, 
  create_time, update_user, update_date, 
  update_time, time_stamp, rec_stat
  )
values (#{busiOppNo,jdbcType=VARCHAR}, #{collaborator,jdbcType=DATE}, #{custNo,jdbcType=VARCHAR}, 
  #{collaborateDate,jdbcType=DATE}, #{createUser,jdbcType=VARCHAR}, #{createDate,jdbcType=VARCHAR}, 
  #{createTime,jdbcType=VARCHAR}, #{updateUser,jdbcType=VARCHAR}, #{updateDate,jdbcType=VARCHAR}, 
  #{updateTime,jdbcType=VARCHAR}, #{timeStamp,jdbcType=TIMESTAMP}, #{recStat,jdbcType=VARCHAR}
  )

mapper.select:

select busi_opp_no, collaborator, cust_no, collaborate_date, create_user, create_date, 
create_time, update_user, update_date, update_time, time_stamp, rec_stat
from oc_busi_opp_collaborator
where busi_opp_no = #{busiOppNo,jdbcType=VARCHAR}
  and collaborator = #{collaborator,jdbcType=DATE}
