mapper.select:
select group_id, group_name, group_type, group_desc, establish_type,cust_num, member_type, establish_user, establish_time, create_user, create_date, create_time, update_user, update_date, update_time, time_stamp, rec_stat from oc_cust_grp where 1=1
and group_name like '%${groupName}%'
and group_type = #{groupType,jdbcType=VARCHAR}
and create_user = #{createUser,jdbcType=VARCHAR}
order by establish_time desc
