mapper.delete:

delete from oc_cust_grp_member
where group_id = #{groupId,jdbcType=VARCHAR}
  and cust_no = #{custNo,jdbcType=VARCHAR}

mapper.select:

select group_id, cust_no, cust_type, group_type, add_time, create_user, create_date, 
create_time, update_user, update_date, update_time, time_stamp, rec_stat
from oc_cust_grp_member
where group_id = #{groupId,jdbcType=VARCHAR}
  and cust_no = #{custNo,jdbcType=VARCHAR}

select grp.group_id, grp.cust_no,b.cust_nam, p.cust_typ as cust_type, grp.group_type, grp.add_time, grp.create_user, grp.create_date, grp.create_time, grp.update_user, grp.update_date, grp.update_time, grp.time_stamp, grp.rec_stat from oc_cust_grp_member grp,ec_cust_name b,ec_cust_per p where 1=1
and grp.group_id=#{groupId}
and grp.cust_no=#{custNo}
and grp.cust_no=b.cust_no and grp.cust_no=p.cust_no and b.rec_stat='0'
