mapper.delete:

delete from oc_grp_cust_trace
where trace_id = #{traceId,jdbcType=VARCHAR}
  and cust_no = #{custNo,jdbcType=VARCHAR}

mapper.select:

select trace_id, cust_no, group_id, establish_date, strategy_id, create_user, create_date, 
create_time, update_user, update_date, update_time, time_stamp, rec_stat
from oc_grp_cust_trace
where trace_id = #{traceId,jdbcType=VARCHAR}
  and cust_no = #{custNo,jdbcType=VARCHAR}

select trace_id, group_id, cust_no, establish_date, strategy_id, create_user, create_date, 
create_time, update_user, update_date, update_time, time_stamp, rec_stat
from oc_grp_cust_trace
where 1=1 

	and trace_id = #{traceId}


	and group_id = #{groupId}


	and cust_no = #{custNo}


	and strategy_id = #{strategyId}


	and establish_date = #{establishDate}

order by establish_date desc
