mapper.delete:

delete from GGCODE
where CODETYPE = #{codetype,jdbcType=VARCHAR}
  and COMPANYCODE = #{companycode,jdbcType=VARCHAR}
  and CODECODE = #{codecode,jdbcType=VARCHAR}

mapper.select:

select CODETYPE, COMPANYCODE, CODECODE, CODECNAME, CODETNAME, CODEENAME, CREATORCODE, 
CREATETIME, UPDATERCODE, UPDATETIME, VALIDDATE, INVALIDDATE, VALIDIND, REMARK, FLAG, 
DISPLAYNO
from GGCODE
where CODETYPE = #{codetype,jdbcType=VARCHAR}
  and COMPANYCODE = #{companycode,jdbcType=VARCHAR}
  and CODECODE = #{codecode,jdbcType=VARCHAR}
